      fetchWrongAnswer(answer)
    }
  }

  for(let i=0;i<questions;i++) {
    const correctAnswer = kanjiArray[Math.floor(Math.random()*kanjiArray.length)]

    questionsArray.push({ 
      question: "here's a question son",
      answers: fetchWrongAnswers(kanjiLevel,4)
    })
  }


  fetchWrongAnswer()
  return answers

}

export const FetchQuizAnswers = (kanjiLevel:number,questions:number) => {

  return(shuffle(questionsArray))
}
