import React,{useEffect, useState} from 'react';
import logo from './logo.svg';
import './App.css';

import FetchKanji from './hooks/FetchKanji'

interface kanjiProps {
  kun_readings: string
}

function App() {
  const [kanjiState,setKanjiState]=useState({kun_readings: ""})
  const [inputState,setInputState]=useState("")

  const handleChange =(event)=>{
    setInputState(event.target.value)
  }


  useEffect(()=>{
    setKanjiState(FetchKanji(inputState))
  })

  return (
    <div className="App">
      <input type="text" onChange={handleChange} />
      {/* <button onClick={(event)=>setKanjiState(event.target.value)}>button</button> */}
      {kanjiState.kun_readings}
    </div>
  );
}

export default App;
