import React,{useState} from 'react'
import {useParams} from 'react-router-dom'
import QuizAnswerButton from './QuizAnswerButton'

import {Button} from '@mantine/core'

interface QuizGameProps {
  questions: Question[]
  endQuiz: Function
}

interface Question{
  question: string
  answers: string[]
}

function shuffle(array:string[]) {
  let currentIndex = array.length,  randomIndex;

  // While there remain elements to shuffle.
  while (currentIndex != 0) {

    // Pick a remaining element.
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex--;

    // And swap it with the current element.
    [array[currentIndex], array[randomIndex]] = [
      array[randomIndex], array[currentIndex]];
  }

  return array;
}

function QuizGame({questions, endQuiz}:QuizGameProps) {
  const [questionIndex,setQuestionIndex]=useState(0)
  const [answered,setAnswered]=useState(true)
  const [colors,setColors]=useState(["blue","blue","blue","blue"])

  const nextQuestion = () => {
    setQuestionIndex(questionIndex+1)
    setAnswered(true)
    setColors(["blue","blue","blue","blue"])
  }

  const answerQuestion = (index:number) => {
    console.log("clicked")
    setAnswered(false)
    let newColors = colors
    if(shuffledAnswers[index]===questions[questionIndex].answers[questions.length-1])
      newColors[index] = "green"
    else
      newColors[index] = "red"

    setColors(newColors)
  }

  const shuffledAnswers = shuffle((questions[questionIndex]).answers)

  console.log(questions[questionIndex].answers)
  console.log(shuffle(questions[questionIndex].answers))


  return (
    <div className="mx-auto w-[70%]">
      <p>{questions[questionIndex].question}</p>
      <div className="grid grid-cols-2 gap-4">
        {shuffledAnswers.map((answer,index)=> 
          <Button 
            key={index} 
            color={colors[index]}
            onClick={()=>(answerQuestion(index))}
            disabled={!answered}
            >
              {answer}
            </Button>)}
      </div>

      <div className='flex flex-row justify-center my-[50px]'>
        {questionIndex < questions.length-1 ? <Button disabled={answered} onClick={()=>nextQuestion()}>Next</Button>: <Button disabled={answered} onClick={()=>endQuiz()}>Finish</Button>}

      </div>  
    </div>
 )
}

export default QuizGame
