import React,{useEffect,useState} from 'react'

function FetchKanjiHook() {


  const [kanjiData,setKanjiData]=useState({kun_readings: ""})

  const fetchData = async (kanji: string) =>{
    const url = `https://kanjiapi.dev/v1/kanji/${kanji}`
    try {
      const result = await fetch(url)
      return await result.json()
    }
    catch {
      console.log("ERROR ERROR ERROR")
    }
  }

  const fetchKanji = (kanji:string) => fetchData(kanji).then(res=>setKanjiData(res))


  return (
    {fetchKanji,kanjiData}
  )
}

export default FetchKanjiHook
