import QuizCard from '../components/QuizCard'
import QuizzesPage from './QuizzesPage'
import NavbarSection from '../components/NavbarSection'



const Home = () =>{

  const [opened, setOpened] = useState(false);

  return (
      <AppShell
        navbarOffsetBreakpoint="sm" 

        className="h-[100%]"
        fixed
        navbar={
          <Navbar
