              <NavbarSection title={"Quiz"} link="/quizzes"/>
            </Navbar.Section>

          </Navbar>
        }

        styles={(theme) => ({
          main: { backgroundColor: theme.colorScheme === 'dark' ? theme.colors.dark[8] : theme.colors.gray[0] },
        })}
      >
          <Outlet  />
			</AppShell>
    );

}

export default Home
